$sql_a = "SELECT * FROM event where idUser = '" . $_SESSION["idUser"] . "' and status = 1";
$result_a = $connection->query($sql_a);

if ($result_a->num_rows > 0) {
        while ($row = $result_a->fetch_assoc()) {
        $records_a[] = $row;
    }
} else {
    echo $connection->error;
    // echo ("Você não possui nenhum evento cadastrado :(");
}

